import logging

import requests

from bs4 import BeautifulSoup


def convert_table_to_json(table_element):
    soup = BeautifulSoup(table_element, 'html.parser')
    tables = soup.find_all('table')

    json_data = {}

    for table in tables:
        rows = table.find_all('tr')
        for row in rows:
            cells = row.find_all('td')
            if len(cells) >= 2:
                error_code = cells[0].text.strip()
                meaning = cells[1].text.strip()
                meaning = meaning.replace('"', '').replace("\\", '')
                json_data[error_code] = meaning

    return json_data


