def translate_to_portuguese(to_translate):
    try:
        url = "https://api.mymemory.translated.net/get"
        params = {
            "q": to_translate,
            "langpair": f"en|pt",
            "de": "jiradevops@delfia.atlassian.net"
        }
        response = requests.get(url, params=params, verify=False)
        data = response.json()
        meaning_translated = data["responseData"]["translatedText"]
    except Exception as error:
        logging.info(error)
        meaning_translated = to_translate
    return meaning_translated


def remove_duplicated_items(input_list):
    unique_list = []
    unique_set = set()

    try:
        for order_list in input_list:
            if len(order_list) == 0:
                continue
            for item in order_list["data"]:
                item["bandeira"] = order_list["bandeira"]
                if not isinstance(item, dict):
                    continue
                item_tuple = tuple(sorted(item.items()))
                if item_tuple not in unique_set:
                    unique_list.append(item)
                    unique_set.add(item_tuple)
        return unique_list
    except Exception as error:
        logging.error(error)
        return unique_list
